import React from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import ShoppingAdd from '@material-ui/icons/AddShoppingCart';

const styles = {
  card: {
    maxWidth: '100%'
  },
  media: {
    // ⚠️ object-fit is not supported by IE 11.
    objectFit: 'cover',
    width: '80%',
    margin: '5% auto'
  },
  button: {
    marginRight: '0',
  },
  iconSmall: {
    fontSize: 20,
  },
};

function ProductCard(props) {
  const { classes,item } = props;
  return (
    <Card className={classes.card}>
      <CardActionArea>
        <CardMedia
          component="img"
          alt={item.name}
          className={classes.media}
          width="50"
          image={item.image_url}
          title={item.name}
        />
        <CardContent>
          <Typography gutterBottom variant="h5" component="h2">
            {item.name}
          </Typography>
          <Typography component="p">
            <strong>Brand:</strong> {item.brand} <strong>Model:</strong> {item.model}
          </Typography>
        </CardContent>
      </CardActionArea>
      <CardActions>
        <span>
            <sup>$</sup>577.00
        </span>
        <Button variant="contained" size="small" className={classes.button} color="primary">
            <ShoppingAdd className={classes.iconSmall} />
            Add
        </Button>
      </CardActions>
    </Card>
  );
}

ProductCard.propTypes = {
  classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(ProductCard);
